glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
out vec4 a_pos;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  v_uv0.x = v_uv0.x*1.0;
  #endif
  v_color = a_color;
  a_pos = pos;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in vec4 a_pos;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
const vec2 repeat = vec2(1, 1);
const vec2 centerPoint = vec2(0.5, 0.5);
const float radius = 0.01;
const float wh_ratio = 0.56;
const float blur = 0.02;
void main () {
  vec4 o = vec4(1,1,1,1);
  vec2 t = v_uv0;
  t.x = fract(v_uv0.x + abs(sin(cc_time.x)));
  float a = 0.01*abs(sin(cc_time.x*2.))*sin(abs(cos(cc_time.x))*v_uv0.x*20.0-cc_time.x*10.0)+abs(cos(cc_time.x/5.0));
  if(t.y > a){
    t.y =  t.y + sin(abs(cos(cc_time.x))*t.x*20.0-cc_time.x*10.0)*0.01;
  }
  o = texture(texture, t);
    float reduce = 1.0-smoothstep(0.,0.03,v_uv0.y - a);
    reduce = max(0.6, reduce);
    if(reduce == 0.6){
      o.r = o.r+0.4;
      o.b = o.b+1.0;
      o.g = o.g+0.5;
    }
    o.a = reduce;
  o*=v_color;
  ALPHA_TEST(o);
  gl_FragColor = o;
}

